package gen;

import java.sql.ResultSet;
import java.sql.SQLException;

public class AuthorValue implements java.io.Serializable {
  // private member variables
  private Integer authorID;
  private String name;
  private String penName;

  // empty constructor
  public AuthorValue(){
    authorID = null; 
    name = null; 
    penName = null; 
  }

  // ResultSet constructor
  public AuthorValue(ResultSet rs) throws SQLException {

    if(rs.getObject(1) != null)
      authorID = new Integer(rs.getInt(1));
    else
      authorID = null;

    name = rs.getString(2);

    penName = rs.getString(3);
  }

  // member variable getters and setters
  
  public Integer getAuthorID() { return authorID; }
  public void setAuthorID(Integer authorID){ this.authorID = authorID; }
  
  public String getName() { return name; }
  public void setName(String name){ this.name = name; }
  
  public String getPenName() { return penName; }
  public void setPenName(String penName){ this.penName = penName; }
}

